import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Sidebar from './homepage/Sidebar';
import Homepage from './homepage/Homepage';
import { findByTestId } from '@testing-library/react';
class App extends Component {

  constructor(){
    super();
    this.state ={
      error: null,
      isLoaded: true,
      items: [],
    }
}
  componentDidMount(){
    this.getSpacexData();
  }
  getSpacexData(filterObject={}){
    this.setState({isLoaded: true})
    let url = "https://api.spaceXdata.com/v3/launches?limit=10";
    Object.keys(filterObject).map(key => {
      if(filterObject[key]){
        url = `${url}&${key}=${filterObject[key]}`
      }
    })
    fetch(url)
      .then(
        (result) => {
          const items = result.json()
          this.setState({
            isLoaded: false,
            items
          });
        },
        (error) => {
          this.setState({
            isLoaded: false,
            error
          });
        }
      )
  }
  applyFilter = (filterObject) =>{
        console.log(filterObject);
        this.getSpacexData(filterObject)
  }

 render(){
  const { error, isLoaded, items } = this.state;
  return (
    <div className="App">
      <div className="sidebar-main">
        <Sidebar
        applyFilter = {this.applyFilter}
        />

      </div>
      <div className="homepage">
          <Homepage
          error = {error}
          isLoaded = {isLoaded}
          items = {items}
          />
      </div>

    </div>
  );
 }

}

export default App;
