import React,{Component } from 'react';

class Sidebar extends Component{
  constructor(){
      super();
      this.handleFilter =this.handleFilter.bind(this)
      this.state ={
        error: null,
        isLoaded: false,
        items: []
      }
  }
  componentDidMount(){
    fetch("https://api.spaceXdata.com/v3/launches?limit=100")
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          items: result
        });
      },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )
  }

