import React,{Component } from 'react';
import './homepage.css';
class Sidebar extends Component{
  constructor(){
      super();
      this.handleFilter = this.handleFilter.bind(this);
      this.state ={
        items: [],
        launch_success:null,
        land_success:null,
        launch_year: null



      }
  }
  handleFilter(event,name, value){
       console.log(event.target.name,name,value)
  }
  render(){
    let launchYear = [2006,2007,2008,2009,2010,2011,2012,2013,2014,2015,2016,2017,2018,2019,2020];
    const { items } = this.state;
       items.map(year => (
          launchYear[year.launch_year] = year.launch_year
          ))
    return(
        <div className="sidebar-main-1">
            <span> Filter </span>
            <div className="sidebar">
               <span> Lunch Year</span>
                <div className="main-2">
                {
                    launchYear.map(year => (
                        <div className="year-bt" id={year}>
                            <button  value={year} name="launch_year" onClick = {(event)=> this.handleFilter(event,"launch_year", year)}>{year}</button>
                        </div>
                    ))
                }
                </div>
                <span>Successful Launch</span>
                <div>
                    <button name="b1" value="true" >True</button>
                    <button name="b1" value="false" >False</button>
                </div>

                <span>Successful Landing</span>
                <div>
                    <button name="b1" value="true" >True</button>
                    <button name="b1" value="false" >False</button>
                </div>

            </div>
        </div>
       );

  }
}

export default Sidebar;
