import React,{Component } from 'react';

class Sidebar extends Component{
  constructor(){
      super();
      this.handleFilter = this.handleFilter.bind(this);
      this.state ={
        error: null,
        isLoaded: false,
        items: []
      }
  }
  handleFilter(e){
       console.log("hello")
  }
  componentDidMount(){
    fetch("https://api.spaceXdata.com/v3/launches?limit=100")
    .then(res => res.json())
    .then(
      (result) => {
        this.setState({
          isLoaded: true,
          items: result
        });
      },
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )
  }

  render(){
    let launchYear = [];
    const { error, isLoaded, items } = this.state;
       items.map(year => (
          launchYear[year.launch_year] = year.launch_year
          ))
    return(
        <div className="main">
            <span> Filter </span>
            <div className="sidebar">
               <span> Lunch Year</span>
                <div className="main-2">
                {
                    launchYear.map(year => (
                        <div className="year-bt" id={year}>
                            <button  value={year} onClick = {(event)=> this.handleFilter(event, year)}>{year}</button>
                        </div>
