import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Sidebar from './homepage/Sidebar';
import Homepage from './homepage/Homepage';
import { findByTestId } from '@testing-library/react';
class App extends Component {

  constructor(){
    super();
    this.state ={
      error: null,
      isLoaded: false,
      items: [],
    }
}
  componentDidMount(){
    this.getSpacexData();
  }
  getSpacexData(filterObject){
    const urlParams = new URLSearchParams(Object.entries(filterObject));
    fetch("https://api.spaceXdata.com/v3/launches?limit=10"+urlParams)
      .then(res => res.json())
      .then(
        (result) => {
            console.log(result)
          this.setState({
            isLoaded: true,
            items: result
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
  }
  applyFilter = (filterObject) =>{
        console.log(filterObject);
        this.getSpacexData(filterObject)
  }

 render(){
  const { error, isLoaded, items } = this.state;
  return (
    <div className="App">
      <div className="sidebar-main">
        <Sidebar
        applyFilter = {this.applyFilter}
        />

      </div>
      <div className="homepage">
          <Homepage
          error = {error}
          isLoaded = {isLoaded}
          items = {items}
          />
      </div>

    </div>
  );
 }

}

export default App;
